import React, { useRef, useState } from 'react';
import Input from '../../UI/Input';
import classes from './MealItemForm.module.css';

const MealItemForm = props => {
    const amountInputRef = useRef();
    const [amountIsValid, setAmountIsValid] = useState(true);
    const sumbitHandler = (event) => {
        event.preventDefault();
        const enteredAmount = amountInputRef.current.value;
        const enteredAmountNumber = +enteredAmount;
        if (enteredAmount.trim().length === 0 || enteredAmountNumber < 0 || enteredAmountNumber > 5) {
            setAmountIsValid(false);
            return;
        }
        props.onAddToCart(enteredAmountNumber);
        setAmountIsValid(true);
    }
    return <form className={classes.form} onSubmit={sumbitHandler}>
        <Input
            ref={amountInputRef}
            label="Amount"
            input={{ id: 'amount', type: 'number', min: '1', max: '5', step: '1', defaultValue: '1' }}>
        </Input>
        <button>+ Add</button>
        {!amountIsValid && <p>please enter valid amount</p>}
    </form>
}

export default MealItemForm;
