import { useSelector, useDispatch } from 'react-redux'
import classes from './Counter.module.css';

const Counter = () => {
  const dispatch = useDispatch();
  const counter = useSelector((state) => {
    return state.counter;
  });

  const showCounter = useSelector(state => {
    return state.showCounter
  })

  const incrementHandler = () => {
    dispatch({ type: 'INCREASE' })
  }

  const incrementByHandler = () => {
    dispatch({ type: 'INCREASEMULTI', val: 5 })
  }

  const decrementHandler = () => {
    dispatch({ type: 'DECREASE' })
  }

  const toggleCounterHandler = () => {
    dispatch({ type: 'TOGGLE' });
  };

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>{counter}</div>
      <div>
        <button onClick={incrementHandler}>Increment</button>
        <button onClick={incrementByHandler}>Increment By 5 </button>
        <button onClick={decrementHandler}>Decrement</button>
      </div>
      {showCounter && <button onClick={toggleCounterHandler}>Toggle Counter</button>}
    </main>
  );
};

export default Counter;
