import { useEffect } from 'react';
import { useSelector,useDispatch } from 'react-redux'
import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import {uiActions} from './store/ui-slice'




function App() {
  const cart = useSelector(state => state.cart);
  useEffect(() => {
    const sendCartData = async () => {
      const reponse = await fetch('https://react-http-b7ee0-default-rtdb.firebaseio.com/cart.json', {
        method: 'PUT',
        body: JSON.stringify(cart)
      })
      if (!reponse.ok) {
        throw new Error('Failed Sending Data');
      }
      const reponseData = await sendCartData.json();
    }
    sendCartData();
  }, [cart]);

  const cartIsVisisble = useSelector(state => {
    return state.ui.cartIsVisible
  })
  return (
    <Layout>
      {cartIsVisisble && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
