import React from 'react';
import { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux'
import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import Notification from './components/UI/Notification';
import { uiActions } from './store/ui-slice'




function App() {
  const cart = useSelector(state => state.cart);
  const notification = useSelector(state => state.ui.notification)
  const dispatch = useDispatch();
  useEffect(() => {
    const sendCartData = async () => {
      dispatch(uiActions.showNotification({
        status: 'pending',
        title: 'Sending',
        message: 'Sending Cart Data'

      }))
      const reponse = await fetch('https://react-http-b7ee0-default-rtdb.firebaseio.com/cart.json', {
        method: 'PUT',
        body: JSON.stringify(cart)
      })
      if (!reponse.ok) {
        throw new Error('Something went wrong')
      }
      dispatch(uiActions.showNotification({
        status: 'success',
        title: 'Success',
        message: 'Sent Cart Data'

      }))
      //const reponseData = await sendCartData.json();
    }
    sendCartData().catch(error => {
      dispatch(uiActions.showNotification({
        status: 'error',
        title: 'Error',
        message: 'Error Sending Data'

      }))
    });
  }, [cart, dispatch]);

  const cartIsVisisble = useSelector(state => {
    return state.ui.cartIsVisible
  })
  return (
    <React.Fragment>
    {notification &&<Notification status = {notification.status} title = {notification.title} message = {notification}></Notification>}
    <Layout>
      {cartIsVisisble && <Cart />}
      <Products />
    </Layout>
    </React.Fragment>

  );
}

export default App;
