import { createSlice } from "@reduxjs/toolkit";
import { uiActions } from "./ui-slice";

const cartSlice = createSlice({
    name: 'cart',
    initialState: {
        items: [],
        totalQuantity: 0,
        totalAmount: 0
    },
    reducers: {
        addItemToCart(state, action) {
            const newItem = action.payload;
            const existingItem = state.items.find(item => {
                return item.id === newItem.id
            })
            state.totalQuantity++;
            if (!existingItem) {
                state.items.push({ id: newItem.id, price: newItem.price, quantity: 1, totalPrice: newItem.price, name: newItem.title })
            } else {
                existingItem.quantity = existingItem.quantity + 1;
                existingItem.totalPrice = existingItem.totalPrice + newItem.price;
            }
        },
        removeItemFromCart(state, action) {
            const removeItemId = action.payload;
            state.totalQuantity--;
            const existingItem = state.items.find(item => {
                return item.id === removeItemId
            })
            if (existingItem.quantity === 1) {
                state.items = state.items.filter(item => item.id !== removeItemId)
            } else {
                existingItem.quantity--;
                existingItem.totalPrice = existingItem.totalPrice - existingItem.price;
            }

        }

    }
})

const sendCartData = (cart) => {
    return async (dispatch) => {
        dispatch(uiActions.showNotification({
            status: 'pending',
            title: 'Sending',
            message: 'Sending Cart Data'

        }))

        const sendRequest = async 

        const reponse = await fetch('https://react-http-b7ee0-default-rtdb.firebaseio.com/cart.json', {
            method: 'PUT',
            body: JSON.stringify(cart)
        })
        if (!reponse.ok) {
            throw new Error('Something went wrong')
        }
        dispatch(uiActions.showNotification({
            status: 'success',
            title: 'Success',
            message: 'Sent Cart Data'

        }))

        dispatch();
    }
}

export const cartAction = cartSlice.actions;
export default cartSlice;
