import React, { useState } from 'react';
import Card from '../UI/Card';
import Button from '../UI/Button';
import ErrorModal from '../UI/ErrorModal';
import classes from './AddUser.module.css';

const AddUser = (props) => {
    const [enteredUserName, setEnteredUserName] = useState("");
    const [enteredUserAge, setEnteredUserAge] = useState("");
    const [error, setError] = useState();

    const addUserHandler = (event) => {
        event.preventDefault();
        if (enteredUserName.trim().length === 0 || enteredUserAge.trim().length === 0) {
            setError({
                title: 'Invalid Input',
                message: 'Please enter valid name and age'
            });
            return;
        }
        if (+enteredUserAge < 0) {
            setError({
                title: 'Invalid Input',
                message: 'Please enter valid age'
            });
            return;
        }
        props.onAddUser(enteredUserName, enteredUserAge);
        console.log(enteredUserName, enteredUserAge)
        setEnteredUserAge('');
        setEnteredUserName('')

    }

    const nameChangeHandler = event => {
        setEnteredUserName(event.target.value);
    }


    const ageChangeHandler = event => {
        setEnteredUserAge(event.target.value);

    }

    const removeErrorHandler = () => {
        setError(null);
    }
    return (

        <div>
            {error && <ErrorModal title={error.title} message={error.message} onremoveError = {removeErrorHandler}/>}
            <Card className={classes.input}>
                <form onSubmit={addUserHandler}>
                    <label>Username:</label>
                    <input type='text' value={enteredUserName} onChange={nameChangeHandler} />
                    <label>Age (Years):</label>
                    <input type='number' value={enteredUserAge} onChange={ageChangeHandler} />
                    <Button type='submit'>Add user</Button>
                </form>
            </Card>
        </div>
    )

}

export default AddUser;
